$(document).ready(function(){$("#selectSearch").change(function(){var a=$(this).val();if(a==""){alert("您的选择有误!");return}$.ajax({url:"Index/?Fid="+a,type:"get",beforeSend:function(){showMessage("","正在请求，请等待！",false,true,true)},dataType:"json",success:function(a){hideMessage(1);if(a.IsSuccess)$("#divModuleList").load(a.ReDirectUrl);else showMessage("<span class='box-error'>失败</span>",a.TipMsg,true,false,false)},error:function(){hideMessage(0);showMessage("<span class='box-error'>错误</span>","网络错误,请稍后再试!",true,false,false)}})})})